mapper.select:
select
template_id, template_name, template_content, template_type, template_status, template_remark, create_time,
update_time, create_user, update_user, delete
from comment_template


        and template_id = #{templateId}


        and template_name = #{templateName}


        and template_content = #{templateContent}


        and template_type = #{templateType}


        and template_status = #{templateStatus}


        and template_remark = #{templateRemark}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from comment_template


        and template_id = #{templateId}


        and template_name = #{templateName}


        and template_content = #{templateContent}


        and template_type = #{templateType}


        and template_status = #{templateStatus}


        and template_remark = #{templateRemark}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
