mapper.select:
select
community_id, community_name, community_header, community_header_id, community_describe, community_user,
create_time, update_time, create_user, update_user, delete
from community


        and community_id = #{communityId}


        and community_name = #{communityName}


        and community_header = #{communityHeader}


        and community_header_id = #{communityHeaderId}


        and community_describe = #{communityDescribe}


        and community_user = #{communityUser}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from community


        and community_id = #{communityId}


        and community_name = #{communityName}


        and community_header = #{communityHeader}


        and community_header_id = #{communityHeaderId}


        and community_describe = #{communityDescribe}


        and community_user = #{communityUser}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
