mapper.select:
select
user_id, user_name, pwd, user_sex, email, role, dept_id, super_admin, status, create_time, update_time,
create_user, update_user, delete
from community_user


        and user_id = #{userId}


        and user_name = #{userName}


        and pwd = #{pwd}


        and user_sex = #{userSex}


        and email = #{email}


        and role = #{role}


        and dept_id = #{deptId}


        and super_admin = #{superAdmin}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from community_user


        and user_id = #{userId}


        and user_name = #{userName}


        and pwd = #{pwd}


        and user_sex = #{userSex}


        and email = #{email}


        and role = #{role}


        and dept_id = #{deptId}


        and super_admin = #{superAdmin}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
