mapper.select:
select
dept_id, dept_name, dept_parent_id, dept_parent_name, create_time, update_time, create_user, update_user, delete
from department


        and dept_id = #{deptId}


        and dept_name = #{deptName}


        and dept_parent_id = #{deptParentId}


        and dept_parent_name = #{deptParentName}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from department


        and dept_id = #{deptId}


        and dept_name = #{deptName}


        and dept_parent_id = #{deptParentId}


        and dept_parent_name = #{deptParentName}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
