mapper.select:
select
goods_id, goods_name, goods_pic, goods_integral, goods_stock, goods_price, create_time, update_time,
create_user, update_user, delete
from goods


        and goods_id = #{goodsId}


        and goods_name = #{goodsName}


        and goods_pic = #{goodsPic}


        and goods_integral = #{goodsIntegral}


        and goods_stock = #{goodsStock}


        and goods_price = #{goodsPrice}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from goods


        and goods_id = #{goodsId}


        and goods_name = #{goodsName}


        and goods_pic = #{goodsPic}


        and goods_integral = #{goodsIntegral}


        and goods_stock = #{goodsStock}


        and goods_price = #{goodsPrice}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
