mapper.select:
select
permission_id, user_id, page_name, type, create_time, update_time, create_user, update_user, delete
from permissions


        and permission_id = #{permissionId}


        and user_id = #{userId}


        and page_name = #{pageName}


        and type = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from permissions


        and permission_id = #{permissionId}


        and user_id = #{userId}


        and page_name = #{pageName}


        and type = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
