mapper.select:
select
role_id, role_name, role_status, create_time, update_time, create_user, update_user, delete
from roles


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_status = #{roleStatus}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from roles


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_status = #{roleStatus}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
