mapper.select:
select
task_id, task_name, task_type, start_time, end_time, dept_id, dept_name, executor, domain_type, task_status,
task_content, create_time, update_time, create_user, update_user, delete
from task


        and task_id = #{taskId}


        and task_name = #{taskName}


        and task_type = #{taskType}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and dept_id = #{deptId}


        and dept_name = #{deptName}


        and executor = #{executor}


        and domain_type = #{domainType}


        and task_status = #{taskStatus}


        and task_content = #{taskContent}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from task


        and task_id = #{taskId}


        and task_name = #{taskName}


        and task_type = #{taskType}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and dept_id = #{deptId}


        and dept_name = #{deptName}


        and executor = #{executor}


        and domain_type = #{domainType}


        and task_status = #{taskStatus}


        and task_content = #{taskContent}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and delete = #{delete}
